
public class UnariEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//post decrement
		int i=30;
		
		i--;
		
		System.out.println(i);
		
		System.out.println(i--);
		
		System.out.println(i);
		
		//pre decrement
		
		int j = 40;
		--j;
		
		System.out.println(j); //39
		
		System.out.println(--j); //38
		
		
	}

}
