package nov30;

public class ForloopEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=10; i++) {
			System.out.println(i);
		}
		
		for(int i=10; i>=1; i--) {
			System.out.println(i);
		}
		
		
		int sum=0;
		int Table= 2;
		
		for(int i=1; i<=10; i++) {
			System.out.println(Table + "x" + i + "=" +2*i);
		}
		
	}

}
